mapper.select:
SELECT share_company_id,valid_flg
	FROM share_company 
		WHERE share_company_id = #{share_company_id}

SELECT com.company_name, em.name, em.phone_number, c.case_id, c.case_title, c.case_description, c.case_budget, c.start_date, c.case_skill, c.case_address, cus.customer_name, res.responsible_name, res.responsible_phone FROM case_share as cs LEFT JOIN `case` as c ON cs.case_id = c.case_id LEFT JOIN share_company as sc ON cs.share_company_id = sc.share_company_id LEFT JOIN company as com ON sc.affiliate_id = com.company_id LEFT JOIN employee as em ON c.employee_id = em.employee_id AND em.employee_type = '責任者' LEFT JOIN customer as cus ON c.customer_id = cus.customer_id LEFT JOIN responsible as res ON c.responsible_id = res.responsible_id
and sc.share_company_id = #{share_company_id}
and c.start_date >= #{start_date} and c.end_date <= #{end_date}
and c.start_date >= #{start_date}
and c.end_date <= #{end_date}
and c.case_skill = #{case_skill}
and c.case_address = #{case_address}
